import { configure } from 'enzyme';
import Adapter from 'enzyme-adapter-react-16';

configure({ adapter: new Adapter() });

import sinon from  'sinon'
import { expect } from 'chai'

import * as Api from '../network/Api'
import { apiWorker } from './workers'
import { articlesFetchFailed } from '../redux/actions/actions'


describe('apiWorker', () => {
    it('on error', done => {
        const error = new Error({messange: "no internet connection"})
        const fakeFetch = sinon.fake.throws(error)
        sinon.replace(Api, 'requestArticles', fakeFetch)

        const gen = apiWorker()

        expect(gen.next().value).to.be.equals(articlesFetchFailed(error))
        expect(gen.next().done).to.be.true
        done()
    })
})

import { call, put } from 'redux-saga/effects'
import { articlesFetchSucceeded, articlesFetchFailed } from '../redux/actions/actions'
import { requestArticles } from '../network/Api'


export function* apiWorker() {
    try {
        const payload = yield call(requestArticles);
        yield put(articlesFetchSucceeded(payload));
    } catch (e) {
        yield put(articlesFetchFailed(e));
    }
}

import { ARTICLES_FETCH_REQUESTED, ARTICLE_CREATE } from "../../consts";
import { createActions } from "redux-actions";


export function requestArticlesFromApi() {
    return { type: ARTICLES_FETCH_REQUESTED }
}

export function createArticle(payload) {
    return { type: ARTICLE_CREATE, payload }
}

export const { articlesFetchSucceeded, articlesFetchFailed } = createActions({
    ARTICLES_FETCH_SUCCEEDED: (articles = []) => ({ articles }),
    ARTICLES_FETCH_FAILED: (error) => ({error})
})

import React from 'react';
import { expect } from 'chai';
import { shallow } from 'enzyme';
import Home from './Home';


describe('<Home />', () => {
  it('rendered the title', done => {
    const wrapper = shallow(<Home />);

    expect(wrapper.text()).to.contain('Articles');
    done();
  });
});

import React from 'react'
import ArticlesList from './components/ArticlesList/ArticlesList'
import NewArticleForm from './components/NewArticleForm/NewArticleForm'


function Home() {
  return (
    <div className="App">
      <h1>Articles</h1>

      <NewArticleForm/>

      <ArticlesList/>
    </div>
  );
}

export default Home

import React from 'react'
import { useSelector, useDispatch } from'react-redux'
import { requestArticlesFromApi } from '../../../../redux/actions/actions'


function ArticlesList(props) {
    const articles = useSelector(state => state.articles)
    const isLoading = useSelector(state => state.areArticlesLoading)
    const dispatch = useDispatch()

    if (isLoading == null) {
        dispatch(requestArticlesFromApi())
    }

    return (
        <ul>
            {articles.map(genArticleLi)}
        </ul>
    )
}

const genArticleLi = (article) => (
    <li key={article.id}>
        {article.title}
    </li>
)

export default ArticlesList

import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { createArticle } from '../../../../redux/actions/actions'


function NewArticleForm() {

    const dispatch = useDispatch()
    const [title, setTitle] = useState('')

    function onSubmitArticle(event) {
        event.preventDefault()

        dispatch(createArticle({title: title}))

        setTitle('')
    }

    function onChangeTitle(event) {
        setTitle(event.target.value)
    }

    return (
        <form onSubmit={onSubmitArticle}>
            <input value={title} onChange={onChangeTitle}/>

            <button type='submit'>
                Adicionar
            </button>
        </form>
    )
}

export default NewArticleForm
